package my.project.dailylexika.config.datahandler;import lombok.RequiredArgsConstructor;import my.project.library.dailylexika.enumerations.Platform;import org.springframework.beans.factory.annotation.Value;import org.springframework.boot.CommandLineRunner;import org.springframework.stereotype.Component;@Component@RequiredArgsConstructorpublic class WordsLoader implements CommandLineRunner {    private final ExcelDataHandler excelDataHandler;    @Value("${file-location.data-excel}")    private String FILE_CLASSPATH;    private static final String FILE_SHEET_FOR_WORD_PACKS_EN = "EN-WordPacks";    private static final String FILE_SHEET_FOR_WORDS_EN = "En-Words";    private static final String FILE_SHEET_FOR_WORD_PACKS_CH = "CH-WordPacks";    private static final String FILE_SHEET_FOR_WORDS_CH = "CH-Words";    @Override    public void run(String... args) {        excelDataHandler.importWordPacks(FILE_CLASSPATH, FILE_SHEET_FOR_WORD_PACKS_EN, Platform.ENGLISH);        excelDataHandler.importWords(FILE_CLASSPATH, FILE_SHEET_FOR_WORDS_EN, Platform.ENGLISH);        excelDataHandler.importWordPacks(FILE_CLASSPATH, FILE_SHEET_FOR_WORD_PACKS_CH, Platform.CHINESE);        excelDataHandler.importWords(FILE_CLASSPATH, FILE_SHEET_FOR_WORDS_CH, Platform.CHINESE);    }}